import { createRouter, createWebHistory } from 'vue-router'; // Cambia la importación
import Estudiantes from './components/EstudiantesList.vue'; // Tu componente de lista de estudiantes
import EditEstudiante from './components/EstudiantesEditar.vue'; // El componente para editar estudiante

// Crea el router usando Vue 3
const router = createRouter({
  history: createWebHistory(), // Usa createWebHistory para el manejo de rutas
  routes: [
    {
      path: '/estudiantes',
      name: 'estudiantes',
      component: Estudiantes, // Componente que muestra la lista de estudiantes
    },
    {
      path: '/edit-estudiante/:id', // :id es un parámetro que se usará para identificar el estudiante
      name: 'editEstudiante',
      component: EditEstudiante, // Componente para editar estudiante
    },
    {
      path: '*',
      redirect: '/estudiantes', // Redirige a la lista de estudiantes si la ruta no existe
    },
  ],
});

export default router;
